package main

import (
	"bytes"
	"fmt"
	"io/ioutil"
	"log"
	"net/http"
	"os"
)

// Example url to shorten.
// http://localhost:8080?

func main() {
	jsonStr := []byte(`{"domain": "s.grocerieslist.app", "target": "http://localhost:8080?"}`)

	client := http.Client{}
	req, err := http.NewRequest("POST", "https://kutt.it/api/v2/links", bytes.NewBuffer(jsonStr))
	req.Header.Set("Content-Type", "application/json")
	req.Header.Set("X-API-KEY", os.Getenv("KUTT_KEY"))
	if err != nil {
		log.Fatalln(err)
	}

	res, err := client.Do(req)
	if err != nil {
		log.Fatalln(err)
	}

	if res.StatusCode == 201 {
		body, err := ioutil.ReadAll(res.Body)
		if err != nil {
			log.Fatalln(err)
		}
		fmt.Println(string(body))
	} else {
		fmt.Println(res.StatusCode)
	}
}
